PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "admins" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "email" varchar(255) DEFAULT '' NOT NULL, "encrypted_password" varchar(255) DEFAULT '' NOT NULL, "reset_password_token" varchar(255), "reset_password_sent_at" datetime, "remember_created_at" datetime, "sign_in_count" integer DEFAULT 0 NOT NULL, "current_sign_in_at" datetime, "last_sign_in_at" datetime, "current_sign_in_ip" varchar(255), "last_sign_in_ip" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "chapters" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "description" text, "created_at" datetime, "updated_at" datetime, "course_id" integer);
CREATE TABLE "courses" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "description" text, "created_at" datetime, "updated_at" datetime, "picture" varchar(255));
CREATE TABLE "media" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "description" text, "audio" varchar(255), "mediumable_id" integer, "mediumable_type" varchar(255), "created_at" datetime, "updated_at" datetime, "video" varchar(255));
CREATE TABLE "practices" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "description" text, "chapter_id" integer, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "theories" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "description" text, "chapter_id" integer, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE "users" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "email" varchar(255) DEFAULT '' NOT NULL, "encrypted_password" varchar(255) DEFAULT '' NOT NULL, "reset_password_token" varchar(255), "reset_password_sent_at" datetime, "remember_created_at" datetime, "sign_in_count" integer DEFAULT 0 NOT NULL, "current_sign_in_at" datetime, "last_sign_in_at" datetime, "current_sign_in_ip" varchar(255), "last_sign_in_ip" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "course_translations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "course_id" integer NOT NULL, "locale" varchar(255) NOT NULL, "created_at" datetime, "updated_at" datetime, "title" varchar(255), "description" text);
CREATE TABLE "chapter_translations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "chapter_id" integer NOT NULL, "locale" varchar(255) NOT NULL, "created_at" datetime, "updated_at" datetime, "title" varchar(255), "description" text);
CREATE TABLE "theory_translations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "theory_id" integer NOT NULL, "locale" varchar(255) NOT NULL, "created_at" datetime, "updated_at" datetime, "title" varchar(255), "description" text);
CREATE TABLE "practice_translations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "practice_id" integer NOT NULL, "locale" varchar(255) NOT NULL, "created_at" datetime, "updated_at" datetime, "title" varchar(255), "description" text);
CREATE TABLE "medium_translations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "medium_id" integer NOT NULL, "locale" varchar(255) NOT NULL, "created_at" datetime, "updated_at" datetime, "title" varchar(255), "description" text, "audio" varchar(255), "video" varchar(255));
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "index_admins_on_email" ON "admins" ("email");
CREATE UNIQUE INDEX "index_admins_on_reset_password_token" ON "admins" ("reset_password_token");
CREATE INDEX "index_chapters_on_course_id" ON "chapters" ("course_id");
CREATE INDEX "index_media_on_mediumable_id_and_mediumable_type" ON "media" ("mediumable_id", "mediumable_type");
CREATE INDEX "index_practices_on_chapter_id" ON "practices" ("chapter_id");
CREATE INDEX "index_theories_on_chapter_id" ON "theories" ("chapter_id");
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE UNIQUE INDEX "index_users_on_email" ON "users" ("email");
CREATE UNIQUE INDEX "index_users_on_reset_password_token" ON "users" ("reset_password_token");
CREATE INDEX "index_course_translations_on_course_id" ON "course_translations" ("course_id");
CREATE INDEX "index_course_translations_on_locale" ON "course_translations" ("locale");
CREATE INDEX "index_chapter_translations_on_chapter_id" ON "chapter_translations" ("chapter_id");
CREATE INDEX "index_chapter_translations_on_locale" ON "chapter_translations" ("locale");
CREATE INDEX "index_theory_translations_on_theory_id" ON "theory_translations" ("theory_id");
CREATE INDEX "index_theory_translations_on_locale" ON "theory_translations" ("locale");
CREATE INDEX "index_practice_translations_on_practice_id" ON "practice_translations" ("practice_id");
CREATE INDEX "index_practice_translations_on_locale" ON "practice_translations" ("locale");
CREATE INDEX "index_medium_translations_on_medium_id" ON "medium_translations" ("medium_id");
CREATE INDEX "index_medium_translations_on_locale" ON "medium_translations" ("locale");
COMMIT;
